import React, { useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

const Card = ({ title, subtitle, icon, onClick }) => {
  const subtitleRef = useRef(null);

  useEffect(() => {
    const subtitleElement = subtitleRef.current;
    if (!subtitleElement) return;

    const createWord = (text, index) => {
      const word = document.createElement("span");
      word.innerHTML = `${text} `;
      word.className = "inline-block m-0 opacity-0 transform translate-y-2/5 transition-none";
      word.style.transitionDelay = `${index * 40}ms`;
      return word;
    }

    const addWord = (text, index) => subtitleElement.appendChild(createWord(text, index));
    const createSubtitle = text => text.split(" ").map(addWord);

    subtitleElement.innerHTML = '';
    createSubtitle(subtitle);
  }, [subtitle]);

  return (
    <div 
      className="aspect-[1/1.6] border-2 border-[#03a9f4] cursor-pointer relative w-[280px] max-w-[calc(33.33%-2rem)] 
                 hover:before:bg-[100%_100%] hover:before:scale-[1.08,1.03] 
                 before:content-[''] before:absolute before:inset-0 before:bg-gradient-to-br 
                 before:from-transparent before:via-[#6200ea] before:to-[#fdd835] 
                 before:bg-[length:300%_300%] before:transition-all before:duration-350 before:ease-in-out before:z-10"
      onClick={onClick}
    >
      <div className="absolute inset-0 z-20 p-5 bg-[radial-gradient(rgba(255,255,255,0.1)_8%,transparent_8%)] 
                      bg-[length:5vmin_5vmin] transition-all duration-350 ease-in-out 
                      hover:bg-[-10%_0%]">
        <h3 className="text-[6vmin] mb-2 text-[#e8eaed] font-normal">{title}</h3>
        <h4 ref={subtitleRef} className="text-sm text-[#e8eaed] font-normal"></h4>
      </div>
      <i className={`absolute bottom-5 left-5 text-2xl text-[rgba(232,234,237,0.5)] transition-colors duration-250 
                     ease-in-out z-20 fa-solid ${icon} group-hover:text-[#e8eaed]`}></i>
    </div>
  );
}

const Buttons = () => {
  const navigate = useNavigate();

  const cards = [
    { title: "School Notice Board", subtitle: "Click to view the latest notices", icon: "fa-book", path: "/notice" },
    { title: "Class Schedule", subtitle: "Click to view the latest class schedule", icon: "fa-calendar", path: "/routine" },
    { title: "Grade IX Gallery", subtitle: "Click to view some photos of Grade IX", icon: "fa-image", path: "/gallery" },
    { title: "Grade IX Chatroom", subtitle: "Click to access the G-IX chatroom", icon: "fa-comment", path: "/chat" },
    { title: "Project Details", subtitle: "Some information about the project", icon: "fa-users", path: "/about" },
  ];

  const handleCardClick = (path) => {
    navigate(path);
  };

  return (
    <div className="bg-[#202124] min-h-screen flex justify-center items-center font-roboto">
      <div className="flex flex-col gap-8 p-8 max-w-[1200px] w-full">
        <div className="flex justify-center gap-8 flex-wrap">
          {cards.slice(0, 3).map((card, index) => (
            <Card 
              key={index} 
              title={card.title} 
              subtitle={card.subtitle} 
              icon={card.icon} 
              onClick={() => handleCardClick(card.path)}
            />
          ))}
        </div>
        <div className="flex justify-center gap-8 flex-wrap">
          {cards.slice(3, 5).map((card, index) => (
            <Card 
              key={index + 3} 
              title={card.title} 
              subtitle={card.subtitle} 
              icon={card.icon} 
              onClick={() => handleCardClick(card.path)}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default Buttons;
