import React, { useState, useEffect, useRef } from 'react';
import './GroupCall.css';
import io from 'socket.io-client';

const socket = io('https://6711c250-f6ca-4f9f-a6e2-2b3df23ce26f-00-3paa249pb63vs.sisko.replit.dev:443');

const GroupCall = ({ user }) => {
  const [isCallActive, setIsCallActive] = useState(false);
  const [participants, setParticipants] = useState([]);
  const [localStream, setLocalStream] = useState(null);
  const [peerConnections, setPeerConnections] = useState({});
  const localAudioRef = useRef(null);

  useEffect(() => {
    socket.on('callParticipantsUpdate', (updatedParticipants) => {
      setParticipants(updatedParticipants);
    });

    socket.on('userJoinedCall', handleUserJoinedCall);
    socket.on('receiveOffer', handleReceiveOffer);
    socket.on('receiveAnswer', handleReceiveAnswer);
    socket.on('receiveIceCandidate', handleReceiveIceCandidate);

    return () => {
      socket.off('callParticipantsUpdate');
      socket.off('userJoinedCall');
      socket.off('receiveOffer');
      socket.off('receiveAnswer');
      socket.off('receiveIceCandidate');
    };
  }, []);

  const toggleCall = async () => {
    if (isCallActive) {
      leaveCall();
    } else {
      await joinCall();
    }
    setIsCallActive(!isCallActive);
  };

  const joinCall = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      setLocalStream(stream);
      localAudioRef.current.srcObject = stream;
      const participant = { id: user.id, name: user.name };
      socket.emit('joinCall', participant);
    } catch (error) {
      console.error('Error accessing microphone:', error);
    }
  };

  const leaveCall = () => {
    socket.emit('leaveCall', user.id);
    if (localStream) {
      localStream.getTracks().forEach(track => track.stop());
    }
    setLocalStream(null);
    Object.values(peerConnections).forEach(pc => pc.close());
    setPeerConnections({});
  };

  const handleUserJoinedCall = async (userId) => {
    const peerConnection = new RTCPeerConnection();
    setPeerConnections(prev => ({ ...prev, [userId]: peerConnection }));

    localStream.getTracks().forEach(track => {
      peerConnection.addTrack(track, localStream);
    });

    peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit('sendIceCandidate', { to: userId, candidate: event.candidate });
      }
    };

    peerConnection.ontrack = (event) => {
      // Handle incoming audio stream
      const audioElement = new Audio();
      audioElement.srcObject = event.streams[0];
      audioElement.play();
    };

    const offer = await peerConnection.createOffer();
    await peerConnection.setLocalDescription(offer);
    socket.emit('sendOffer', { to: userId, offer });
  };

  const handleReceiveOffer = async ({ from, offer }) => {
    const peerConnection = new RTCPeerConnection();
    setPeerConnections(prev => ({ ...prev, [from]: peerConnection }));

    localStream.getTracks().forEach(track => {
      peerConnection.addTrack(track, localStream);
    });

    peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit('sendIceCandidate', { to: from, candidate: event.candidate });
      }
    };

    peerConnection.ontrack = (event) => {
      // Handle incoming audio stream
      const audioElement = new Audio();
      audioElement.srcObject = event.streams[0];
      audioElement.play();
    };

    await peerConnection.setRemoteDescription(offer);
    const answer = await peerConnection.createAnswer();
    await peerConnection.setLocalDescription(answer);
    socket.emit('sendAnswer', { to: from, answer });
  };

  const handleReceiveAnswer = async ({ from, answer }) => {
    const peerConnection = peerConnections[from];
    await peerConnection.setRemoteDescription(answer);
  };

  const handleReceiveIceCandidate = ({ from, candidate }) => {
    const peerConnection = peerConnections[from];
    peerConnection.addIceCandidate(new RTCIceCandidate(candidate));
  };

  return (
    <>
      <button onClick={toggleCall} className="call-icon">
        ðŸ“ž
      </button>
      {isCallActive && (
        <div className="call-modal">
          <div className="call-content">
            <h2>Group Call</h2>
            <ul className="participants-list">
              {participants.map((participant) => (
                <li key={participant.id} className="participant-item">
                  <span className="participant-name">{participant.name}</span>
                  <span className="participant-status">ðŸŸ¢</span>
                </li>
              ))}
            </ul>
            <button onClick={leaveCall} className="hang-up-button">Hang Up</button>
          </div>
          <audio ref={localAudioRef} muted />
        </div>
      )}
    </>
  );
};

export default GroupCall;
